Move findWhiteKing(int board[8][8]) {
	Move k;
	for (int i = 0; i < 8; i++) {
		for (int j = 0; j < 8; j++) {
			if (board[i][j] == 4) {
				k.x = j;
				k.y = i;
				return k;
			}
		}
	}
}

Move findBlackKing(int board[8][8]) {
	Move k;
	for (int i = 0; i < 8; i++) {
		for (int j = 0; j < 8; j++) {
			if (board[i][j] == 11) {
				k.x = j;
				k.y = i;
				return k;
			}
		}
	}
}

