int convertColumn(char column) {
    if (column >= 'a' && column <= 'h') {
        return column - 'a';
    } else {
        return -1;
    }
}
int convertRow(char row) {
    if (row >= '1' && row <= '8') {
        return row - '1';
    } else {
        return -1;
    }
}

