void makeMove(int board[8][8], BestMove mv, Move* whtKing, Move* blckKing) {
	board[mv.y2][mv.x2] = board[mv.y1][mv.x1];
	board[mv.y1][mv.x1] = 6;
	if (board[mv.y2][mv.x2] == 4) {
		whtKing->x = mv.x2;
		whtKing->y = mv.y2;
	}
	if (board[mv.y2][mv.x2] == 11) {
		blckKing->x = mv.x2;
		blckKing->y = mv.y2;
	}
	if (board[mv.y2][mv.x2] == 7 && mv.y2 == 0) {
		board[mv.y2][mv.x2] = 12;
	}
	if (board[mv.y2][mv.x2] == 0 && mv.y2 == 7) {
		board[mv.y2][mv.x2] = 5;
	}
}

int eval(int board[8][8]) {
	int Eval = 0;
	int ev = 2;
	for (int i = 0; i < 8; i++) {
		for (int j = 0; j < 8; j++) {
			if (board[i][j] < 6) {
				if (board[i][j] == 0) {
					Eval += ev*whitePawnEval[i][j];
					Eval += 1000;
				}
				else {
					if (board[i][j] == 1) {
						Eval += ev*knightEval[i][j];
						Eval += 3000;
					}
					else {
						if (board[i][j] == 2) {
							Eval +=ev* whitebishopEval[i][j];
							Eval += 3300;
						}
						else {
							if (board[i][j] == 3) {
								Eval += ev*whiteRookEval[i][j];
								Eval += 5000;
							}
							else {
								if (board[i][j] == 4) {
									Eval += 2137000;
								}
								else {
									if (board[i][j] == 5) {
										Eval += ev*queenEval[i][j];
										Eval += 8000;
									}
								}
							}
						}
					}
				}
			}
			if (board[i][j] > 6) {
				if (board[i][j] == 7) {
					Eval -= ev*blackPawnEval[i][j];
					Eval -= 1000;
				}
				else {
					if (board[i][j] == 8) {
						Eval -= ev*knightEval[i][j];
						Eval -= 3000;
					}
					else {
						if (board[i][j] == 9) {
							Eval -= ev*blackbishopEval[i][j];
							Eval -= 3300;
						}
						else {
							if (board[i][j] == 10) {
								Eval -= ev*blackRookEval[i][j];
								Eval -= 5000;
							}
							else {
								if (board[i][j] == 11) {
									Eval -= 2137000;
								}
								else {
									if (board[i][j] == 12) {
										Eval -= ev*queenEval[i][j];
										Eval -= 8000;
									}
								}
							}
						}
					}
				}
			}
		}
	}
	return Eval;
}

