void reverseRows(int board[8][8]) {
	for (int i = 0; i < 4; ++i) {
		for (int j = 0; j < 8; ++j) {
			int temp = board[i][j];
			board[i][j] = board[7 - i][j];
			board[7 - i][j] = temp;
		}
	}
}

